import React, {Component} from 'react';

import './post-list-item.css';

export default class PostListItem extends Component {

    render() {
        const {label, onDelete, onToggleImportant, onToogleLiked} = this.props;
        const {important, like} = this.state;

        let classNames = 'app-list-item d-flex justify-content-between';

        if(like) {
            classNames += ' like';
        }

        if(important) {
            classNames += ' important';
        }    

        return(
            <div className={classNames}>
                <span className="app-list-item-label" onClick={onToogleLiked}>
                    {label}
                </span>
                <div className="d-flex justify-content-center align-items-center">
                        <button className="btn-star btn-sm" onClick={onToggleImportant}>
                        <i className="fa fa-star"></i>
                    </button>
                    <button className="btn-trash btn-sm" onClick={onDelete}>
                        <i className="fa fa-trash-o"></i>
                    </button>
                    <i className="fa fa-heart"></i>
                </div>
            </div>
            );
        }
}
